mapper.select:
SELECT 得意先マスタ.得意先コード AS tokuisakiCode , 得意先マスタ.得意先名 AS tokuisakiName , 得意先マスタ.得意先カナ名 AS tokuisakiNameKana FROM 得意先マスタ
会社区分コード = #{kaishaKubunCode} AND ＳＬ_利用フラグ = #{slUseFlag}
AND 得意先コード = #{tokuisakiCode}
AND 得意先名 LIKE CONCAT('%', #{tokuisakiName}, '%')
AND 得意先カナ名 LIKE CONCAT('%', #{tokuisakiNameKana}, '%')

SELECT
  得意先マスタ.得意先コード AS value
  , 得意先マスタ.得意先名 AS text
FROM
  得意先マスタ 

  会社区分コード = #{kaishaKubunCode}
  AND ＳＬ_利用フラグ = #{slUseFlag}

    AND 得意先コード = #{tokuisakiCode}


ORDER BY 得意先コード ASC

SELECT
  得意先マスタ.得意先コード AS tokuisakiCode
  , 得意先マスタ.得意先名 AS tokuisakiName
  , 得意先マスタ.得意先カナ名 AS tokuisakiNameKana
FROM
  得意先マスタ 

  会社区分コード = #{kaishaKubunCode}
  AND 得意先コード = #{tokuisakiCode}
  AND ＳＬ_利用フラグ = #{slUseFlag}

SELECT 得意先コード AS tokuisakiCode, 得意先名 AS tokuisakiName FROM 得意先マスタ WHERE 会社区分コード = 1 AND SL_利用フラグ = 1
AND 得意先コード = #{loginTokuisakiCode}
ORDER BY 得意先コード ASC
